import { use } from "passport";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { set } from "mongoose";


class Search extends React.Component {
  constructor(){
    super();
    this.state = {
      name: "",
      patients: []
    }
  }

  onSubmit = (e) => {
    console.log(this.state.name);
    axios
        .get(`http://localhost:3000/api/consults/search/${this.state.name}`)
                                                  //?email=${datausr.email} (req.query.email) in the backend
        .then((res) => this.setState({...this.state,patients:res}, () => console.log(this.state.patients)))

  }

  onChange = (e) => {{
    this.setState({
      name: e.target.value
    }, () => {
      console.log(this.state.name);
    });
  }
}
  render() {
    return (
      <div>
        <input onChange= {this.onChange} type="text" id="name"  value= {this.state.name}/>
        <button onClick= {this.onSubmit}></button>
      </div>
    );
  }
}

export default Search;
