import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, withRouter } from "react-router-dom";

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      name: "",
      patients: [],
    };
  }

  onSubmit = (e) => {
    console.log(this.state.name);
    axios
      .get(`http://localhost:3000/api/consults/search/${this.state.name}`)
      //?email=${datausr.email} (req.query.email) in the backend
      .then((res) =>
        this.setState({ ...this.state, patients: res.data }, () =>
          console.log(this.state.patients)
        )
      );
  };

  onChange = (e) => {
    {
      this.setState(
        {
          name: e.target.value,
        },
        () => {
          console.log();
        }
      );
    }
  };
  render() {
    return (
      <div>
        <div className="header" style= {{display: "list-item"}}>
          <div className="back-btn">
            <Link to="/dashboard" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
          </div>
          <img
            className="logo"
            src={require("../images/MedBase-Logo.png")}
            alt="Not found"
            style={{position: "fixed", paddingTop: "100px"}}
          />
        </div>
        <div className="body-sec">
          <input
            onChange={this.onChange}
            type="text"
            id="name"
            value={this.state.name}
            style= {{paddingTop: "200px", width: "100px"}}
          />
          <button onClick={this.onSubmit}></button>
          <div>
            <ul>
              {this.state.patients.map((item) => {
                return (
                  <ul style={{ display: "list-item", margin: "20px" }}>
                    <li>birthdate: {item.birthdate.toString()}</li>
                    <li>country: {item.country.toString()}</li>
                    <li>direction1: {item.direction1.toString()}</li>
                    <li>email: {item.email.toString()}</li>
                    <li>name: {item.name.toString()}</li>
                    <li>phone number: {item.phoneNumber.toString()}</li>
                  </ul>
                );
              })}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Search;
