import { use } from "passport";
import React, { useEffect, useState } from "react";
import axios from "axios";


function Search() {

  const [name, setName] = useState("hola")

  const datausr = {
    name: "Manuel Jose",
    email: "supelDotol@gmail.com"
  }

  function onChaange(e){
    setName({
      [e.target.id]: e.target.value
    })
  }

  function getUsers(usrName){
    return axios
          .get(`http://localhost:3000/api/consults/search/${datausr.name}`)
                                                  //?email=${datausr.email} (req.query.email) in the backend
          .then((res) => console.log(res.data))
  }


  return <div>
  <h1>Insert user</h1>
  <input type="text" id= "name" value= {name} onChange= {onChaange}/>

  </div>;
}

export default Search;
